import React, {useEffect, useState} from "react";
import {Container, Grid, Grow, Paper, AppBar, TextField, Button, Chip} from "@mui/material";
import Posts from "../Posts/Posts";
import Form from "../Form/Form";
import {useDispatch} from "react-redux";
import {getPosts} from "../../actions/posts";
import Paginator from "../Pagination";
import {useNavigate, useLocation} from "react-router-dom";
import {ComponentChipInput} from 'chip-input';


function useQuery(){
    return new URLSearchParams(useLocation().search);
}


const Home = () => {

    const dispatch = useDispatch();
    const [currentId,setCurrentId] = useState(null);
    useEffect(() => {
        dispatch(getPosts());
    },[dispatch])

    const navigate = useNavigate();
    const query = useQuery();
    const page = query.get('page') || 1;
    const searchQuery = query.get('searchQuery');

    return (
        <Grow in>
            <Container maxWidth={'xl'}>
                <Grid sx={{
                    flexDirection:'row',
                    ['@media (max-width:600px)']: {
                        flexDirection:'column-reverse',
                    }
                }} container justify="space-between" alignItems="stretch" spacing={3}>
                    <Grid item xs={12} sm={6} md={9}>
                        <Posts setCurrentId={setCurrentId}/>
                    </Grid>
                    <Grid item xs={12} sm={6} md={3}>
                        <AppBar sx={{borderRadius: 4,
                            marginBottom: '1rem',
                            display: 'flex',
                            padding: '16px',}}
                        position={'static'} color={'inherit'}>
                            <TextField
                                name={'search'}
                                variant={'outlined'}
                                label={'Search Memories'}
                                fullWidth
                                value={'search'}
                            />
                        </AppBar>
                        <Form currentId={currentId} setCurrentId={setCurrentId}/>
                        <Paper sx={{
                            borderRadius: 4,
                            marginTop: '1rem',
                            padding: '16px',
                        }}
                        elevation={6}>
                            <Paginator/>
                        </Paper>
                    </Grid>
                </Grid>
            </Container>
        </Grow>
    )
}
export default Home;
