import React from "react";
import {useState, useEffect} from "react";
import {TextField, Button, Typography, Paper} from "@mui/material";
import FileBase from "react-file-base64"
import {useDispatch} from "react-redux";
import {createPost,updatePost} from "../../actions/posts";
import {useSelector} from "react-redux";
const Form = (props) => {
    const dispatch = useDispatch();
    const post = useSelector((state) => props.currentId ? state.posts.find((p) => p._id === props.currentId) : null);
    const [postData,setPostData] = useState({
        creator: '',
        title: '',
        message: '',
        tags: '',
        selectedFile: ''
    });
    useEffect(() => {
        if(post)
            setPostData(post);
    },[post])

    const handleSubmit = (e) => {
        e.preventDefault();
        if(props.currentId){
            dispatch(updatePost(props.currentId,postData));
        }else {
            dispatch(createPost(postData));
        }
    }
    const clear = () => {}
    return(
        <Paper sx={{
            padding: '50px',
        }}>
            <form action="" autoComplete="off" noValidate onSubmit={handleSubmit} style={{
                display: 'flex',
                flexWrap: 'wrap',
                justifyContent: 'center',
            }}>
                <Typography variant="h6">${props.currentId ? 'Edit' : 'Create'} a memory</Typography>
                <TextField sx={{marginBottom: '10px'}} name="creator" label="Creator" variant="outlined" fullWidth value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value})}/>
                <TextField sx={{marginBottom: '10px'}} name="title" label="Title" variant="outlined" fullWidth value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value})}/>
                <TextField sx={{marginBottom: '10px'}} name="message" label="Message" variant="outlined" fullWidth value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value})}/>
                <TextField sx={{marginBottom: '10px'}} name="tags" label="Tags" variant="outlined" fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value})}/>
                <div style={{
                    width: '97%',
                    margin: '10px 0',
                }}>
                    <FileBase type="file" multiple={false} onDone={({base64}) => setPostData({...postData, selectedFile: base64})}/>
                </div>
                <Button sx={{
                    marginBottom: '10px',
                }} variant="contained" type="submit" color="primary" size="large" fullWidth>
                    Submit
                </Button>
                <Button variant="contained" onClick={clear} color="secondary" size="small" fullWidth>
                    Clear
                </Button>
            </form>
        </Paper>
    )
}
export default Form;
