import axios from "axios";

const API = axios.create({baseURL: 'http://localhost:5000' })


export const getPosts = () => API.get(url);
export const createPost = (post) => API.post(url,post);
export const updatePost = (currentId,post) => API.patch(url + '/' + currentId,post);
export const deletePost = (currentId) => API.delete(url + '/' + currentId);
export const likePost = (currentId) => API.patch(url + '/' + currentId + '/likePost');
